            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1832, '36015B', 11483, 'Cumputerized treatment planning—complex', '電腦治療規劃--複雜', '1.指使用三度空間電腦軟體做放射治療之設計，包括順形放射治療、強度調控放射治療、立體定位放射治療等技術，以至近接治療之規劃等。2.需附治療計畫。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1833, '36016B', 540, 'Blood products irradiation', '血品放射線處理', '限下列情況使用：１．胎兒子宮內輸血、新生兒輸血或換血、早產而輸血２．免疫力效能不足、受損、減弱者３．骨髓或周邊血液細胞移植者及其它器官移植者４．血緣關係之親屬捐血５．其他可能因輸血而引起之移植物對抗宿主疾病者（GVHD)');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1834, '36017B', 880, 'Emergency blood products irradiation', '血品急診放射線處理', '限下列情況使用: 1.胎兒子宮內輸血、新生兒輸血或換血、早產而輸血。 2.免疫力效能不足、受損、減弱者。3.骨髓或周邊血液細胞移植者及其他器官移植者。 4.血緣關係之親屬捐血。5.其他可能因輸血而引起之移植物對抗宿主疾病者(GVHD)。');");
