            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1995, '45082B', 800, 'Family therapy', '家族治療 (60分鐘)', '註:運用家庭動力或系統等理論，以治療精神疾病病人。限精神科醫師或精神科社工師(員)申報，並需附精神科專科醫師之醫囑及簽名。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1996, '45085B', 1031, 'Psychiatric diagnostic interview(time) -adult', '精神科診斷性會談(次)-成人', '註：1.包括病史、心理狀態評估、社會功能評估及病人治療之計畫(安置)等。2.限精神科初診(含新住院病人)或他科要求會診時申報。3.限由精神專科醫師施行並於病歷詳細記載治療情形。4.申報時須附載有該精神科專科醫師簽名之治療紀錄。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1997, '45087C', 344, 'Re-educative individual psychotherapy-adult', '特殊心理治療-成人', '註:利用特殊心理治療技術，以協助病人了解自我、形成病識感、提升病人適應環境技巧、降低病人的主觀挫折及不良行為，本項治療限由精神科醫師或臨床心理師執行，申報時並應附精神科專科醫師之醫囑及簽名 。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1998, '45088C', 430, 'Re-educative individual psychotherapy- 6 to 15 years old', '特殊心理治療-6歲至15歲', '註:利用特殊心理治療技術，以協助病人了解自我、形成病識感、提升病人適應環境技巧、降低病人的主觀挫折及不良行為，本項治療限由精神科醫師或臨床心理師執行，申報時並應附精神科專科醫師之醫囑及簽名 。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (1999, '45089C', 515, 'Re-educative individual psychotherapy-under 6 years old', '特殊心理治療-6歲以下', '其註:利用特殊心理治療技術，以協助病人了解自我、形成病識感、提升病人適應環境技巧、降低病人的主觀挫折及不良行為，本項治療限由精神科醫師或臨床心理師執行，申報時並應附精神科專科醫師之醫囑及簽名 。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2000, '45090C', 1460, 'Intensive individual psychotherapy-6 to 15 years old', '深度心理治療（每40分鐘）-6歲至15歲', '註:利用深度心理治療技術，以協助病人改善客體關係（object relation）、強化心理防衛機轉、解決內在衝突或改善其認知行為偏差。本項治療限由精神科專科醫師執行，申報時並應附治療紀錄。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2001, '45091C', 1718, 'Intensive individual psychotherapy-under 6 years old', '深度心理治療（每40分鐘）-6歲以下', '註:利用深度心理治療技術，以協助病人改善客體關係（object relation）、強化心理防衛機轉、解決內在衝突或改善其認知行為偏差。本項治療限由精神科專科醫師執行，申報時並應附治療紀錄。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2002, '45092B', 1203, 'Psychiatric diagnostic interview(time) -6 to 15 years old', '精神科診斷性會談(次)-6至15歲', '註:1.包括病史、心理狀態評估、社會功能評估及病人治療之計畫(安置)等。2.限精神科初診(含新住院病人)或他科要求會診時申報。3.限由精神專科醫師施行並於病歷詳細記載治療情形。4.申報時須附載有該精神科專科醫師簽名之治療紀錄。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2003, '45093B', 1375, 'Psychiatric diagnostic interview(time) -under 6', '精神科診斷性會談(次)，6歲以下', '註:1.包括病史、心理狀態評估、社會功能評估及病人治療之計畫(安置)等。2.限精神科初診(含新住院病人)或他科要求會診時申報。3.限由精神專科醫師施行並於病歷詳細記載治療情形。4.申報時須附載有該精神科專科醫師簽名之治療紀錄。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2004, '45094C', 129, 'Re-educative group psychotherapy', '特殊團體心理治療（每人次）', '註:經由團體前準備、團體進行、團體後之整理、團體報告之撰寫，利用特殊團體心理治療技術及治療因子，以協助團體中的病人了解自我、形成病識感、提升適應環境技巧、降低主觀挫折及不良行為。本項治療限由精神科醫師或臨床心理師執行。申報時應附精神科專科醫師之醫囑及簽名，一次最多以十五人為限。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2005, '45095C', 325, 'Special Occupational therapy', '特殊職能治療(次)', '註:1.一般治療項目3項或特殊治療項目1項。2.合計治療時間達80分鐘者。3.特殊治療項目包含:(1)感覺統合治療　(2)心理調適功能訓練 (3)知覺認知訓練 (4)執行功能訓練 (5)職業復健 (6)環境改造 (7)兒童職能治療。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2006, '45096C', 1031, 'Biofeedback therapy assessment and planning', '生理回饋治療之評估與計劃', '註:限精神科醫師或臨床心理師執行，每次療程限申報一次，申報時並應附紀錄精神科專科醫師簽章。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2007, '45097C', 129, 'Group Biofeedback therapy', '團體生理回饋治療之執行 (每次)', '註:限精神科專科醫師指導下由精神治療團隊之各專業成員執行，每次療程限申報12次。一次治療最多以五人為限。申報時並需附個別之生理回饋儀之數據報告。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (2008, '45098C', 387, 'Psychophysiological function examination-6 to 15 years old', '生理心理功能檢查-6歲至15歲', '註:包括MSE(Mini Mental State Test， BDI等)。');");
