            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4172, '90003C', 3010, 'Endodontics', '恆牙根管治療（三根以上）', '1.本項費用包括Ｘ光費用、局部麻醉、斷髓治療、根管沖洗、換藥、擴大及根管充填等各有關治療項目費用在內。2.本項目於全部治療過程完畢併加90015C專案申報；如未完成，改以90015C申報。(期間限申報一次診療費)3.申報費用時，需附治療前與治療後Ｘ光片以為審核；全民健康保險牙醫門診總額特殊醫療服務計畫之適用對象無法配合照射X光片不在此限。4.六十天之同一牙位重新治療為同一療程。5.如同牙位九十天內重覆申報90001C、90002C、90003C、90019C、90020C者，則以支付點數最高者申報。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4173, '90004C', 150, 'Endodontic emergency treatment', '齒內治療緊急處理', '1.需記載具體處置內容。2.此項不得申請轉診加成。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4174, '90005C', 800, 'Primary tooth pulpotomy', '乳牙斷髓處理', '1.需附治療前Ｘ光片以為審核(Ｘ光片費用已內含)。2.六十天內，不得再申報90015C。3.麻醉費用內含。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4175, '90006C', 240, 'Removal of s-p crown', '去除縫成牙冠', '1.申報費用時，需附治療前Ｘ光片及治療後Ｘ光片或相片(規格需為3*5吋以上且可清晰判讀)以為審核（Ｘ光片或相片費用已內含），合併拔牙的切除牙橋處置，可免附術後X光片。2.此項不得申請轉診加成。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4176, '90007C', 500, 'Removal of casting crown', '去除鑄造牙冠', '1.需附治療前Ｘ光片及治療後Ｘ光片或相片(規格需為3*5吋以上且可清晰判讀)以為審核（Ｘ光片或相片費用已內含），合併拔牙的切除牙橋處置，可免附術後X光片或相片。2.申報 90007C 後不得另行申報OD。(覆髓除外)。3.此項不得申請轉診加成。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4177, '90008C', 1000, 'Removal of post', '去除釘柱', '申報費用時，需附治療前與治療後Ｘ光片以為審核(X光片費用已內含)。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4178, '90010C', 900, 'Retrograde filling', '根尖逆充填術', '以根管數計算，需檢附術後Ｘ光片（Ｘ光片費用已內含）');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4179, '90011C', 1000, 'Replantation', '牙齒再植術', '不包括根管治療，需檢附術前及術後Ｘ光片以為審核（Ｘ光片費用已內含）');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4180, '90012C', 150, 'Rubber dam appliance', '橡皮障防濕裝置', '註：1.銀粉、複合樹脂、玻璃離子體及複合體充填時，橡皮障防濕裝置視病情需要使用。2.使用橡皮障防濕裝置時，需檢附Ｘ光片或相片(規格需為3*5吋以上且可清晰判讀)佐證。（X光片或相片費用已內含）。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4181, '90013C', 500, 'Apexification or Apexogenesis-anterior teeth', '根尖成形術或根尖生成術-前牙', '1.限開根尖式根管者(open apex)。2.處置完成（根尖成形）後需檢附術前、術後Ｘ光片（術後X光片費用另計）申報，處置完成前之追蹤檢查，每三個月可視需要申報診察費及Ｘ光費用。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4182, '90014C', 1000, 'Apexification or Apexogenesis-posterior teeth', '根尖成形術或根尖生成術-後牙', '1.限開根尖式根管者(open apex)。2.處置完成（根尖成形）後需檢附術前、術後Ｘ光片（術後X光片費用另計）申報，處置完成前之追蹤檢查，每三個月可視需要申報診察費及Ｘ光費用。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4183, '90015C', 600, 'Canal enlarge & debridement', '根管開擴及清創', '註：1.單獨申報此費用時，需檢附未完成充填前Ｘ光片以為審核。全民健康保險牙醫門診總額特殊醫療服務計畫之適用對象不在此限。2.已申報斷髓處理，60天內不得再申報此項費用。3. 六十天內不得重覆申報。4.本項目Ｘ光片費用已內含。5.麻醉費用內含。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4184, '90016C', 1010, 'Milk tooth pulpectomy', '乳牙根管治療', '1.本項費用包括Ｘ光費用、局部麻醉、拔髓治療、根管沖洗、換藥、擴大及充填等各有關治療項目費用在內。2.本項目於全部治療過程完畢併加90015C專案申報(如已申報90005C，六十天內不得再併加90015C申報)；如未完成，改以90015C申報。(期間限申報一次診療費)3.申報費用時，需附治療前與治療後Ｘ光片以為審核。全民健康保險牙醫門診總額特殊醫療服務計畫之適用對象，如情況特殊無法配合，得不在此限，但應於病歷詳細記錄原因。4.90天內不得重複申報。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4185, '90017C', 600, 'tooth pulpotomy', '恆牙斷髓處理', '註：限全民健康保險牙醫門診總額特殊醫療服務計畫適用對象之牙醫醫療服務申報。');");
            sqLiteDatabase.execSQL("INSERT INTO 'nhi' VALUES (4186, '90018C', 1410, 'Milk tooth pulpectomy', '乳牙多根管治療', '註：1.本項費用包括Ｘ光費用、局部麻醉、拔髓治療、根管沖洗、換藥、擴大及充填等各有關治療項目費用在內。2.本項目於全部治療過程完畢併加 90015C專案申報（如已申報90005C，六十天內不得再併加90015C申報）；如未完成，改以90015C申報。（期間限申報一次診療費）3.申報費用時，需附治療前與治療後Ｘ光片以為審核。全民健康保險牙醫門診總額特殊醫療服務計畫之適用對象，如情況特殊無法配合，得不在此限，但應於病歷詳細記錄原因。4.九十天內不得重複申報。');");
